const inputItems = $input.all();
const outputItems = [];

console.log("Input length:", inputItems.length);

try {
  for (let i = 0; i < inputItems.length; i++) {
    const currentItem = inputItems[i].json;

    // If current item is an array (e.g., array of products), loop over it
    const items = Array.isArray(currentItem) ? currentItem : [currentItem];

    for (let j = 0; j < items.length; j++) {
      const item = items[j];

      // Extract price from various formats
      let price = 0;
      if (item.price) {
        if (typeof item.price === 'object' && item.price.value) {
          price = parseFloat(item.price.value);
        } else if (typeof item.price === 'string') {
          const match = item.price.match(/[\d.]+/);
          price = match ? parseFloat(match[0]) : 0;
        } else if (typeof item.price === 'number') {
          price = item.price;
        }
      }

      const processedItem = {
        site: 'Amazon',
        title: item.title || '',
        model: item.asin || '',
        brand: item.brand || '',
        price: price,
        rating: item.stars ? parseFloat(item.stars) : 0,
        url: item.url || '',
        image: item.thumbnailImage || ''
      };

      outputItems.push(processedItem);
    }
  }
} catch (error) {
  console.error("Error processing items:", error);
}

if (outputItems.length === 0) {
  outputItems.push({
    site: 'Amazon',
    title: 'No items processed',
    model: '',
    brand: '',
    price: 0,
    rating: 0,
    url: '',
    image: ''
  });
}

console.log("Returning", outputItems.length, "processed items");
return outputItems;

const inputItems = $input.all();
const outputItems = [];
console.log("Target input length:", inputItems.length);
try {
  for (let i = 0; i < inputItems.length; i++) {
    const currentItem = inputItems[i].json;

    // If current item is an array (e.g., array of products), loop over it
    const items = Array.isArray(currentItem) ? currentItem : [currentItem];
    for (let j = 0; j < items.length; j++) {
      const item = items[j];

      // Extract price from Target format
      let price = 0;
      if (item.price && item.price.current_retail) {
        price = parseFloat(item.price.current_retail);
      }

      // Get brand name from nested structure
      let brand = '';
      if (item.primary_brand && item.primary_brand.name) {
        brand = item.primary_brand.name;
      }

      // Get image URL
      let imageUrl = '';
      if (item.images && item.images.primary_image_url) {
        imageUrl = item.images.primary_image_url;
      }

      const processedItem = {
        site: 'Target',
        title: item.title || '',
        model: item.tcin || '',
        brand: brand,
        price: price,
        rating: item.rating_score ? parseFloat(item.rating_score) : 0,
        url: item.url || '',
        image: imageUrl
      };
      outputItems.push(processedItem);
    }
  }
} catch (error) {
  console.error("Error processing Target items:", error);
}
if (outputItems.length === 0) {
  outputItems.push({
    site: 'Target',
    title: 'No Target items processed',
    model: '',
    brand: '',
    price: 0,
    rating: 0,
    url: '',
    image: ''
  });
}
console.log("Returning", outputItems.length, "processed Target items");
return outputItems;

// Get all items from the merged data
const allItems = $input.all().map(item => item.json);

// Separate Amazon and Target items
const amazonItems = allItems.filter(item => item.site === 'Amazon');
const targetItems = allItems.filter(item => item.site === 'Target');

// Calculate summary statistics
const amazonStats = {
  totalCount: amazonItems.length,
  averagePrice: amazonItems.reduce((sum, item) => sum + parseFloat(item.price || 0), 0) / amazonItems.length,
  priceRange: {
    min: Math.min(...amazonItems.map(item => parseFloat(item.price || 0))),
    max: Math.max(...amazonItems.map(item => parseFloat(item.price || 0)))
  },
  topBrands: [...new Set(amazonItems.map(item => item.brand))].filter(Boolean).slice(0, 5)
};

const targetStats = {
  totalCount: targetItems.length,
  averagePrice: targetItems.reduce((sum, item) => sum + parseFloat(item.price || 0), 0) / targetItems.length,
  priceRange: {
    min: Math.min(...targetItems.map(item => parseFloat(item.price || 0))),
    max: Math.max(...targetItems.map(item => parseFloat(item.price || 0)))
  },
  topBrands: [...new Set(targetItems.map(item => item.brand))].filter(Boolean).slice(0, 5)
};

// Prepare data structure for the AI
const dataForAI = `
LAPTOP PRICE DATA SUMMARY:

AMAZON DATA:
- Total laptops: ${amazonStats.totalCount}
- Average price: $${amazonStats.averagePrice.toFixed(2)}
- Price range: $${amazonStats.priceRange.min.toFixed(2)} to $${amazonStats.priceRange.max.toFixed(2)}
- Top brands: ${amazonStats.topBrands.join(', ')}

TARGET DATA:
- Total laptops: ${targetStats.totalCount}
- Average price: $${targetStats.averagePrice.toFixed(2)}
- Price range: $${targetStats.priceRange.min.toFixed(2)} to $${targetStats.priceRange.max.toFixed(2)}
- Top brands: ${targetStats.topBrands.join(', ')}

COMPLETE PRODUCT DATA:
${JSON.stringify(allItems, null, 2)}
`;

// Output the prepared data
return [{
  json: {
    summary: dataForAI,
    amazonItems: amazonItems,
    targetItems: targetItems,
    stats: {
      amazon: amazonStats,
      target: targetStats
    }
  }
}];
